frontApp.controller("categoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categoryPosts=[];n.posts=[];n.idCategory=angular.element("#idCategory").val();n.tinTucs=[];n.cauHoiThuongGaps=[];n.khuyenMais=[];n.veNoiDias=[];n.veQuocTes=[];n.veTheoHangs=[];n.veTheoLoais=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent=="1"&&n.categoryPosts.push(t)})});t.get("/API/PostsAPI?att=idCategory&&value="+n.idCategory).success(function(t){n.posts=t});t.get("/API/PostsAPI?att=idCategory&&value=2").success(function(t){n.tinTucs=t});t.get("/API/PostsAPI?att=idCategory&&value=3").success(function(t){n.cauHoiThuongGaps=t});t.get("/API/PostsAPI?att=idCategory&&value=4").success(function(t){n.khuyenMais=t});t.get("/API/PostsAPI?att=idCategory&&value=6").success(function(t){n.veNoiDias=t});t.get("/API/PostsAPI?att=idCategory&&value=5").success(function(t){n.veQuocTes=t});t.get("/API/PostsAPI?att=idCategory&&value=7").success(function(t){n.veTheoHangs=t});t.get("/API/PostsAPI?att=idCategory&&value=8").success(function(t){n.veTheoLoais=t});t.get("/API/PostsAPI/").success(function(t){angular.forEach(t,function(t){t.featured==1&&t.idCategory==2&&n.baiVietTops.push(t)})})}]);