frontApp.controller("categoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categoryPosts=[];n.posts=[];n.idCategory=angular.element("#idCategory").val();n.khuyenMais=[];n.cauHoiThuongGaps=[];n.tinTucs=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent=="1"&&n.categoryPosts.push(t)})});t.get("/API/PostsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategory==n.idCategory&&n.posts.push(t);t.featured==1&&t.idCategory==2&&n.baiVietTops.push(t);t.idCategory==2&&n.tinTucs.push(t);t.idCategory==3&&n.cauHoiThuongGaps.push(t);t.idCategory==4&&n.khuyenMais.push(t)})})}]);