frontApp.controller("categoryProductController",["$scope","$http","$window","CategoryProduct",function(n,t,i,r){n.categoryProducts=[];n.products=[];n.idCategoryProduct=angular.element("#idCategoryProduct").val();n.product={};n.id;n.spNoiBats=[];n.remVais=[];n.remCuons=[];n.remSaos=[];n.remChucNangs=[];n.remKhacs=[];n.chanGas=[];n.remVaiProducts=[];n.remCuonProducts=[];n.remSaoProducts=[];n.remChucNangProducts=[];n.remKhacProducts=[];n.chanGaProducts=[];t.get("/API/CategoryProductsAPI/").success(function(t){angular.forEach(r.getallCategory(t),function(t){t.idCategoryParent==1&&n.categoryProducts.push(t);t.idCategoryParent==2&&n.remVaiProducts.push(t);t.idCategoryParent==3&&n.remCuonProducts.push(t);t.idCategoryParent==4&&n.remSaoProducts.push(t);t.idCategoryParent==5&&n.remChucNangProducts.push(t);t.idCategoryParent==6&&n.remKhacProducts.push(t);t.idCategoryParent==25&&n.chanGaProducts.push(t)})});n.show=function(i){n.id=i;t.get("/API/ProductsAPI/"+i).success(function(t){n.product=t})};t.get("/API/ProductsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategoryProduct==n.idCategoryProduct&&n.products.push(t);t.feature=="1"&&n.spNoiBats.push(t);t.idCategoryProduct==2&&n.remVais.push(t);t.idCategoryProduct==3&&n.remCuons.push(t);t.idCategoryProduct==4&&n.remSaos.push(t);t.idCategoryProduct==5&&n.remChucNangs.push(t);t.idCategoryProduct==6&&n.remKhacs.push(t);t.idCategoryProduct==25&&n.chanGas.push(t)})})}]);